import {React, useRef, useEffect} from 'react'
import './HeroStyle.css'
import cloud1 from '../img/cloud1.svg'
import cloud2 from '../img/cloud2.svg'
import moon from '../img/moon.svg'
import { gsap } from "gsap";


gsap.registerPlugin(MotionPathPlugin);

const Hero = () => {
    const heroText = useRef();
    const moonSVG =  useRef();
    const rectRef = useRef();

    useEffect(() => {
        gsap.from(heroText.current, { 			
        scrollTrigger : {
        toggleActions : 'play none restart restart',
        trigger       : '.hero-text'
        },
        opacity       : 0,
        delay         : 1.1,
        z             : 100,
        duration      : 2,
        stagger       : 0.5 
        });
    });

      useEffect(() => {
        gsap.to(moonSVG.current, {
			scrollTrigger : {
				toggleActions : 'play none restart restart',
				trigger       : '.moon'
			},
			delay    : 3,
			rotation : 360,
			duration : 3,
		});
    });

    useEffect(() => {
        gsap.to(rectRef.current, {
            motionPath: {
                path: "#path",
                align: "#path",
                alignOrigin: [0.5, 0.5],
                autoRotate: true
            },
            duration: 5,
            ease: "power1.inOut"
        });
    })



    return (
        <div id="top" className='hero'>
            <div className="hero-content">
                <div className="hero-text">
                    <h1 title="Luna Cuevas" className="intro">Luna Cuevas</h1>
                    <div className="hero-p" ref={heroText}>
                        <p>Developer</p>
                        <p>&</p>
                        <p>Designer</p>
                    </div>
                </div>
                <div className="moon">
                    <img className="cloud1" src={cloud1} alt="left cloud svg" />
                    <img className="moon1" ref={moonSVG} src={moon} alt="moon svg" />
                    <img className="cloud2" src={cloud2} alt="right cloud svg" />
                </div>
            </div>
            <svg width="100%" height="100%" viewBox="-20 0 557 190" id="svg">
                <circle cx="100" cy="100" r="3" />
                <circle cx="300" cy="20" r="3" />
                <path id="path" d="M9,100c0,0,18.53-41.58,49.91-65.11c30-22.5,65.81-24.88,77.39-24.88c33.87,0,57.55,11.71,77.05,28.47c23.09,19.85,40.33,46.79,61.71,69.77c24.09,25.89,53.44,46.75,102.37,46.75c22.23,0,40.62-2.83,55.84-7.43c27.97-8.45,44.21-22.88,54.78-36.7c14.35-18.75,16.43-36.37,16.43-36.37"/>
                <g id="rect" ref={rectRef}>
                    <rect width="85" height="30" fill="dodgerblue" />
                    <text x="10" y="19" font-size="14">SVG &lt;rect&gt;</text>
                </g>
            </svg>
        </div>
    )
}

export default Hero
