import {React, useRef, useEffect} from 'react'
import upButton from '../img/upbutton.svg'
import './ToTopButtonStyle.css'
import { HashLink } from 'react-router-hash-link';
import { gsap } from "gsap";

const ToTopButton = () => {

    const upButton = useRef();

    useEffect(() => {
        gsap.to(upButton.current, {
            scrollTrigger : {
                toggleActions : 'play none restart restart',
                trigger       : '.info'
                },
        })
    })


    return (
        <div className='svg'>
            <HashLink smooth to='#top'><img src={upButton} alt="to top button" /></HashLink>
        </div>
    )
}

export default ToTopButton
