import {React, useRef, useEffect} from 'react';
import upbutton from '../img/upbutton.svg';
import './ToTopButtonStyle.css';
import { HashLink } from 'react-router-hash-link';
import { gsap } from "gsap";

const ToTopButton = () => {

    const el = useRef();
    const q = gsap.utils.selector(el)

    useEffect(() => {
        gsap.from(q('.lorem'), { 			
        scrollTrigger : {
        toggleActions : 'play none restart restart',
        trigger       : '.lorem'
        },
        opacity       : 0,
        delay         : 1.1,
        z             : 100,
        duration      : 2,
        stagger       : 0.5 });
      });


    return (
        <div className='svg'>
            <HashLink smooth to='#top'><img src={upbutton} alt="to top button" /></HashLink>
        </div>
    )
}

export default ToTopButton
