import React from 'react'
import upbutton from '../img/upbutton.svg'
import './ToTopButtonStyle.css'
import { HashLink } from 'react-router-hash-link';

const ToTopButton = () => {
    myID = document.getElementById("svg");

    var myScrollFunc = function() {
      var y = window.scrollY;
      if (y >= 800) {
        myID.className = "svg show"
      } else {
        myID.className = "svg hide"
      }
    };
    window.addEventListener("scroll", myScrollFunc);


    return (
        <div id='svg'>
            <HashLink smooth to='#top'><img src={upbutton} alt="to top button" /></HashLink>

        </div>
    )
}

export default ToTopButton
