import React, { useState } from 'react'
import emailjs from 'emailjs-com'
import './ContactStyle.css'
import Button from '../Button/Button'

const Contact = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [emailSent, setEmailSent] = useState(false);

    const submit = () => {
        if (name && email && message) {
            const serviceId = 'service_neh1oii';
            const templateId = 'template_vdw3ffh';
            const userId = 'user_JcD1331LVSdIeKGHTgDqA';
            const templateParams = {
                name,
                email,
                message
            };

            emailjs.send(serviceId, templateId, templateParams, userId)
                .then(response => console.log(response))
                .then(error => console.log(error));

            setName('');
            setEmail('');
            setMessage('');
            setEmailSent(true);
        } else {
            alert('Please fill in all fields.');
        }
    }
    return (
        <div className='contact-container'>
            <h1 id='contact' className='title'>Contact Me</h1>
            <div id="contact-form">
                <div className='name-email'>
                    <input type="text" placeholder="Your Name" value={name} onChange={e => setName(e.target.value)} />
                    <input type="email" placeholder="Your email address" value={email} onChange={e => setEmail(e.target.value)} />
                </div>


                <textarea placeholder="Your message" value={message} onChange={e => setMessage(e.target.value)}></textarea>
                <div onClick={submit} className='button'>
                <Button  text={'Send Message'} />
                </div>
                <span className={emailSent ? 'visible' : null}>Thank you for your message, we will be in touch in no time!</span>
            </div>
            <div className='contact-text'>
                <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Quod vitae at dignissimos quis, necessitatibus molestiae numquam voluptatem placeat cupiditate reiciendis.</p>

            </div>
        </div>
    );
}

export default Contact
